import styles from './styles.module.scss'

import { MdKeyboardArrowLeft } from 'react-icons/md'
import { MdKeyboardArrowRight } from 'react-icons/md'

export function Carousel() {
  const slideData = [
    {
      image: 'https://grafica.noalvo.com.br/servicos-graficos/imagens/banner-de-fotos.jpg'
    },
    {
      image: 'https://c4.wallpaperflare.com/wallpaper/500/442/354/outrun-vaporwave-hd-wallpaper-preview.jpg'
    },
    {
      image: 'https://wp.radiojhero.com/wp-content/uploads/2020/10/6f7455b4f10b241ad463057762f3170f.jpg'
    },
    {
      image: 'https://cdn.akamai.steamstatic.com/steam/apps/431960/ss_39ed0a9730b67a930acb8ceed221cc968bee7731.1920x1080.jpg'
    }
  ]

  return (
    <div className={styles.container}>
      {slideData.map(slide => (
        <div className={styles.slider}>
          <div className={styles.item}>
            <img src={styles.image} alt="banner" />
          </div>
        </div>
      ))}
      <div className={styles.slider}>
        <div className={styles.item}
          style={{right: '300%'}}
        >
          <img src="https://grafica.noalvo.com.br/servicos-graficos/imagens/banner-de-fotos.jpg" alt="banner" />
        </div>
        <div className={styles.item}
          style={{right: '300%'}}
        >
          <img src="https://c4.wallpaperflare.com/wallpaper/500/442/354/outrun-vaporwave-hd-wallpaper-preview.jpg" alt="banner" />
        </div>
        <div className={styles.item}
          style={{right: '300%'}}
        >
          <img src="https://wp.radiojhero.com/wp-content/uploads/2020/10/6f7455b4f10b241ad463057762f3170f.jpg" alt="banner" />
        </div>
        <div className={styles.item}
          style={{right: '300%'}}
        >
          <img src="https://cdn.akamai.steamstatic.com/steam/apps/431960/ss_39ed0a9730b67a930acb8ceed221cc968bee7731.1920x1080.jpg" alt="banner" />
        </div>
      </div>
      <button className={styles.previousBtnCarousel}>
        <MdKeyboardArrowLeft />
      </button>
      <button className={styles.nextBtnCarousel}>
        <MdKeyboardArrowRight />
      </button>
    </div>
  )
}
