import { useEffect, useState } from 'react'
import { Button } from '../../components/common/button'
import { IUser } from '../../global/interfaces'
import { UsersRepository } from '../../repository/Users'

import styles from './styles.module.scss'

export default function ConfigAccount() {
  const [currentUser, setCurrentUserUser] = useState<IUser | any>()
  const [nome, setNome] = useState('')
  const [email, setEmail] = useState('')
  const [cpf, setCpf] = useState('')
  const [senha, setSenha] = useState('')

  useEffect(() => {
    const getCurrentUser = async () => {
      const userId = Number(localStorage.getItem('userId'))

      const user = new UsersRepository()
      const getUser = await user.getById(userId)
      setCurrentUserUser(getUser)
      setNome(getUser[0].nome)
      setNome(getUser[0].email)
      setNome(getUser[0].cpf)
      setNome(getUser[0].senha)

      console.log(nome)
      console.log(email)
      console.log()
    }
    getCurrentUser()
  }, [])

  return (
    <div className={styles.container}>
      <div className={styles.card}>
        <h2>Informações da sua conta</h2>
        {currentUser?.map((user: IUser) => (
          <div key={Math.random()} style={{ background: 'red' }}>
            <input
              className={styles.customInput}
              value={user.nome}
              type='text'
              name='nome'
              onChange={e => setNome(e.target.value)}
              placeholder='Nome'
            />
            <input
              className={styles.customInput}
              value={user.email}
              type='email'
              name='email'
              onChange={e => setEmail(e.target.value)}
              placeholder='Email'
            />
            <input
              className={styles.customInput}
              value={user.cpf}
              type='text'
              name='cpf'
              onChange={e => setCpf(e.target.value)}
              placeholder='CPF'
            />
            <input className={styles.customInput}
              value={user.senha}
              type='text'
              name='senha'
              onChange={e => setSenha(e.target.value)}
              placeholder='Nova senha'
            />
            <br />
          </div>
        ))}
        <Button type='button' btnName='Salvar' />
      </div>
    </div>
  )
}
