import styles from "./styles.module.scss";

import { MdAddShoppingCart } from "react-icons/md";
import { Link } from "react-router-dom";
import { SyntheticEvent, useContext } from "react";
import { CarrinhoContext } from "../../../providers/carrinho";

interface CardProps {
  image: string
  title: string
  price: string
  id: number
}

export function Card(props: CardProps) {
  const selectedProducts = new Array();
  const { carrinho, setCarrinho } = useContext(CarrinhoContext)

  function showMessageTest(e: SyntheticEvent, id: number) {
    e.preventDefault();
    selectedProducts.push(id)
    setCarrinho([...carrinho, selectedProducts])
  }

  return (
    <div className={styles.card}>
      <Link to={`/productDetails/${props.id}`}>
        <div className={styles.productImage}>
          <img src={props.image} alt="product" />
        </div>
        <div className={styles.productInfo}>
          <div className={styles.productName}>{props.title}</div>
          <div className={styles.productPrice}>{props.price}</div>
          <button onClick={e => showMessageTest(e, props.id)} className={styles.addToCar}>
            <MdAddShoppingCart onClick={e => showMessageTest(e, props.id)} />
          </button>
        </div>
      </Link>
    </div>
  );
}
