import { Button } from '../../components/common/button'
import { Input } from '../../components/common/input'
import { Range } from '../../components/common/range'

import styles from './styles.module.scss'

import { Link, Redirect } from 'react-router-dom'
import { Form } from '@unform/web'
import { useForm } from "react-hook-form"

import { IUser } from '../../global/interfaces'
import RegisterUser from './registerUser'
import { useState } from 'react'
import { Message } from '../../components/common/message'
import verifyIfEmailExist from './verifyIfEmailExist'

const Signup: React.FC = () => {
  const { register, handleSubmit } = useForm()
  const [emailExist, setEmailExist] = useState(false)
  const [status, setStatus] = useState<number | unknown>(0)
  const [showError, setShowError] = useState(false)

  const onSubmit = async (data: IUser) => {
    setEmailExist(await verifyIfEmailExist(data.email))
    const statusCode = !await verifyIfEmailExist(data.email) && await RegisterUser(data)

    setStatus(statusCode)
    setShowError(statusCode === 201 && !emailExist ? false : true)
  }

  return (
    <>
      <div id={styles.loginContainer}>
        <div id={styles.logoContainer}>
          <span>M</span>
        </div>
        <Form onSubmit={handleSubmit(onSubmit)}>
          <Input type="text" name="nome" register={register("nome")} placeholder="UsuÃ¡rio" required />
          <Input type="email" name="email" register={register("email")} placeholder="Email" required />
          <Input type="text" name="cpf" register={register("cpf")} placeholder="CPF" maxLength={14} mask required />
          <Input type="password" name="senha" register={register("senha")}  placeholder="Senha" required />
          <Link to="/login">Fazer login</Link>
          <Button type="submit" btnName="Cadastrar-se"/>
        </Form>
        {emailExist && <Message type="error">Email jÃ¡ cadastrado</Message>}
        {showError && <Message type="error">Erro ao cadastrar usuÃ¡rio</Message>}
        {status === 201 && !emailExist && <Redirect to="/login" />}
      </div>
      <Range />
    </>
  )
}

export default Signup
