import React, { useState, useEffect } from 'react'

import axios from 'axios'

interface IProducts {
  title: string,
  image: string,
  preco: string,
}

export type ProductsContextType = {
  products: IProducts[];
  setProducts: (products: IProducts[]) => void;
  isLoaded: boolean;
  setIsLoaded: (state: boolean) => void;
};

export const ProductsContext = React.createContext<ProductsContextType>({
  products: [],
  setProducts: text => console.warn('no Product text'),
  isLoaded: false,
  setIsLoaded: (state: boolean) => console.warn('no state set')
});

export const ProductsProvider: React.FC = ({ children }) => {
  const [products, setProducts] = React.useState<IProducts>({ title: "", image: "", preco: "" });
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    async function fetchProductsData() {
      try {
        const response = await axios('http://localhost:5000/products')
        const data = response.data
        setProducts(data)
        setIsLoaded(true)
      } catch(e) {
        console.error(e)
      }
    }

    fetchProductsData()
  }, [])

  return (
    <ProductsContext.Provider value={{products, setProducts, isLoaded, setIsLoaded}}>
      {children}
    </ProductsContext.Provider>
  )
}
