import { useCallback, useState } from 'react'
import styles from './styles.module.scss'

interface PropsInput {
  type: string
  name: string
  placeholder: string
  required?: boolean
  mask?: string
}

export function Input({ type, name, placeholder, required, mask }: PropsInput) {
  const [cpf, setCpf] = useState('')

  const formatData = useCallback((event: React.ChangeEvent<HTMLInputElement>, mask: string) => {
    console.log(mask)
    setCpf(event.target.value.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, "$1.$2.$3-$4"))
  }, [
    mask === 'cpf' && cpf
  ])

  return (
    <>
      <input
        type={type}
        name={name}
        placeholder={placeholder}
        className={styles.customInput}
        value={cpf}
        maxLength={14}
        required={required}
        onChange={event => mask && formatData(event, mask)}
      />
    </>
  )
}
