import styles from './styles.module.scss'

import { useContext, useState } from 'react'
import { ProductsContext } from '../../../providers/products'
import { Spinner } from '../../common/spinner'
import { Card } from '../../common/card'
import { SearchContext } from '../../../providers/saerch'

export function ProductList() {
  const { products, isLoaded } = useContext(ProductsContext)
  const [ warning, setWarning ] = useState('Confira os nossos produtos')
  const { search } = useContext(SearchContext)


  const getContainerHeight = !isLoaded ? {height: '80vh'} : {}

  interface IProduct {
    title: string,
    image: string,
    preco: string,
  }

  console.log(search)

  function myFilter(product: IProduct) {
    const researched = product.title.toLowerCase().includes(search)
    return researched && product
  }

  return (
    <>
      <h1 id={styles.advertising}>
        {(products?.filter(myFilter).length === products.length) && 'Confira os nossos produtos'}
        {(products?.filter(myFilter).length === 0) && 'Produto não encontrado'}
        {(products?.filter(myFilter).length > 0) && 'Produtos encontrados: ' + products?.filter(myFilter).length}
      </h1>
      <div id={styles.productList} style={getContainerHeight}>
        {!isLoaded && <Spinner />}
        {products?.filter(myFilter)
          .map((product: IProduct, i: number) => (
            <div className={styles.cardContainer} key={i}>
              <Card image={product.image} title={product.title} price={product.preco} />
            </div>
          ))}
      </div>
    </>
  )
}
