import styles from './styles.module.scss'

import axios from 'axios'

import { useEffect, useState, useContext } from 'react'

import { Spinner } from '../../common/spinner'
import { Card } from '../../common/card'
import { SearchContext } from '../../../providers/search'

export function ProductList() {
  interface IProduct {
    image: string;
    title: string;
    preco: string;
  }

  const [products, setProducts] = useState<IProduct[]>()
  const [isLoaded, setIsLoaded] = useState(false)
  const [quantityOfProducts, setQuantityOfProducts] = useState(0)
  const { search } = useContext(SearchContext)

  // TODO: create a product interface

  const getContainerHeight = !isLoaded ? {height: '80vh'} : {}

  useEffect(() => {
    async function fetchProductsData() {
      try {
        const response = await axios('http://localhost:5000/products')
        const data = response.data
        setProducts(data)
        setIsLoaded(true)
      } catch(e) {
        console.error(e)
      }
    }

    fetchProductsData()
  }, [])

  return (
    <>
      <h1 id={styles.advertising}>
        {search === '' ? 'Confira os nossos produtos' : 'Produtos encontrados:'}
      </h1>
      <div id={styles.productList} style={getContainerHeight}>
        {!isLoaded && <Spinner />}
        {products?.filter((product: IProduct): any => {
          const researchedProduct = product.title.toLowerCase().includes(search.toLowerCase());
          return researchedProduct && product;
        }).map((product: IProduct, i: number) => (
            <>
              <div className={styles.cardContainer}>
                <Card image={product.image} title={product.title} price={product.preco} />
              </div>
              {(i === 0) && <h1 key={i}>oi</h1>}
            </>
        ))}
      </div>
    </>
  )
}
