import styles from "./styles.module.scss";

import { MdAddShoppingCart, MdRemoveShoppingCart } from "react-icons/md";
import { Link } from "react-router-dom";
import { SyntheticEvent, useEffect, useState } from "react";

interface CardProps {
  image: string
  title: string
  price: string
  id: number
}

const selectedProductsId: number[]  = []

export function Card(props: CardProps) {
  const [carrinho, setCarrinho] = useState<number[] | any>([])

  useEffect(() => {
    const saved = localStorage.getItem('selectedProductsId')?.split(',').map(id => Number(id))
    setCarrinho([...saved] || [])
  }, [])

  function addToCart(e: SyntheticEvent, id: number) {
    e.preventDefault()
    setCarrinho([...carrinho, id])
    selectedProductsId.push(id)
    setToLocalStorage(selectedProductsId)
  }

  function removeFromCart(e: SyntheticEvent, id: number) {
    e.preventDefault()
    const updatedCart = carrinho.filter(productId => productId !== id)
    setCarrinho(updatedCart)
    selectedProductsId.splice(selectedProductsId.indexOf(id), 1)
    setToLocalStorage(selectedProductsId)
  }

  function setToLocalStorage(selectedProductsId: number[]) {
    localStorage.setItem('selectedProductsId', selectedProductsId.toString())
  }

  return (
    <div className={styles.card}>
      <Link to={`/productDetails/${props.id}`}>
        <div className={styles.productImage}>
          <img src={props.image} alt="product" />
        </div>
        <div className={styles.productInfo}>
          <div className={styles.productName}>{props.title}</div>
          <div className={styles.productPrice}>{props.price}</div>
          {carrinho.indexOf(props.id) === -1 ?
          <button onClick={e => addToCart(e, props.id)} className={styles.addToCart}>
            <MdAddShoppingCart />
          </button> :
          <button onClick={e => removeFromCart(e, props.id)} className={styles.removeFromCart}>
            <MdRemoveShoppingCart />
          </button>}
        </div>
      </Link>
    </div>
  );
}
