import { useState, useEffect } from 'react'
import { Button } from '../../components/common/button'

import styles from './styles.module.scss'

export default function CompletePurchase() {
  const [endereco, setEndereco] = useState('')
  const [telefone, setTelefone] = useState('')
  const [cidade, setCidade] = useState('')

  useEffect(() => {
    fetch('http://localhost:5000/pedidos', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        endereco,
        telefone,
        cidade,
        userId: localStorage.getItem('userId'),
        selectedProductIds: localStorage.getItem('selectedProductIds'),
        selectedProductQtds: localStorage.getItem('selectedProductQtds')
      })
    })
  }, [endereco, telefone, cidade])

  return (
    <div className={styles.container}>
      <div className={styles.card}>
        <h2>Informações da sua conta</h2>
        <input
          value={endereco}
          className={styles.customInput}
          type='text'
          name='nome'
          onChange={e => setEndereco(e.target.value)}
          placeholder='Endereço'
        />
        <input
          value={telefone}
          className={styles.customInput}
          type='text'
          name='email'
          onChange={e => setTelefone(e.target.value)}
          placeholder='Telefone'
        />
        <input
          value={cidade}
          className={styles.customInput}
          type='text'
          name='cidade'
          onChange={e => setCidade(e.target.value)}
          placeholder='Cidade'
        />
        <br />
        <Button
          type='button'
          btnName='Finalizar compra'
        />
      </div>
    </div>
  )
}
