import styles from './styles.module.scss'
import { Layout } from "../../components/layouts"
import { useEffect, useState } from 'react'
import { IProduct } from '../../global/interfaces'
import { RepositoryProduct } from '../../repository/Products'

const ProductDetails = () => {
  const [product, setProduct] = useState<IProduct>()
  const [amountOfProduct, setAmountOfProduct] = useState(1)

  function amountOfProductMiddleware(value: number, prodId: number | undefined) {
    if (value > 0) {
      setAmountOfProduct(value)
    }
  }

  useEffect(() => {
    window.scrollTo(0, 0)

    const getProductById = async () => {
      const productId = Number((window as any).location.pathname.split('/')[2])
      const repositoryProduct = new RepositoryProduct()

      const [product] = await repositoryProduct.getById(productId)
      setProduct(product)
    }

    getProductById()
  }, [])

  return (
    <Layout>
      <div className={styles.container}>
        <div className={styles.productDetailsCard}>
          <div className={styles.productImage}>
            <img src={product?.image} alt="product name" />
          </div>
          <div className={styles.productInfo}>
            <div className={styles.productName}>
              {product?.title}
            </div>
            <div className={styles.productPrice}>
              {product?.preco}
            </div>
            <button className={styles.buyBtn}>Comprar agora</button>
            <button className={styles.addToCartBtn}>Adicionar ao carrinho</button>
            <label htmlFor='quantidade'>Quantidade:</label>
            <input
              type="number"
              id="quantidade"
              value={amountOfProduct}
              onChange={e => amountOfProductMiddleware(Number(e.target.value), product?.id)}
              placeholder='quantidade'
            />
          </div>
        </div>
        <div className={styles.horizontalRange} />
      </div>
    </Layout>
  )
}

export default ProductDetails
