import { Button } from '../../components/common/button'
import { Input } from '../../components/common/input'
import { Range } from '../../components/common/range'
import styles from './styles.module.scss'

import { IUser } from '../../global/interfaces';
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';

const Login: React.FC = () => {
  const [user, setUser] = useState('')
  const [password, setPassword] = useState('')

  const [users, setusers] = useState<IUser[]>([]);

  useEffect(() => {
    async function fetchUsersData() {
      try {
        const response = await axios('http://localhost:5000/users')
        const data = response.data
        setusers(data)
      } catch(e) {
        console.error(e)
      }
    }

    fetchUsersData()
  }, [])

  function join() {
    console.log(users)
  }

  function onChanged(e: any) {
    console.log(e.target.name)
  }

  return (
    <>
      <div id={styles.loginContainer}>
        <div id={styles.logoContainer}>
          <span>M</span>
        </div>
        <Input type="text" name="user" placeholder="UsuÃ¡rio" />
        <Input type="password" name="password" placeholder="Senha" onChange={onChanged} />
        <Button btnName="Entrar" onClick={join} />
        <Link to="/">Cadastrar-se</Link>
      </div>

      <Range />
    </>
  )
}

export default Login
