import { useCallback, useState } from 'react'
import styles from './styles.module.scss'

interface PropsInput {
  type: string
  name: string
  placeholder: string
  maxLength?: number
  required?: boolean
  maskCpf?: boolean
}

export function Input({ type, name, placeholder, maxLength, required, maskCpf }: PropsInput) {
  const [cpf, setCpf] = useState('')

  const formatCpf = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
    setCpf(event.target.value.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, "$1.$2.$3-$4"))
  }, [cpf])

  return (
    <>
      <input
        type={type}
        name={name}
        placeholder={placeholder}
        maxLength={maxLength}
        className={styles.customInput}
        value={maskCpf && cpf}
        required={required}
        onChange={event => maskCpf && formatCpf(event)}
      />
    </>
  )
}
