import { Button } from '../../components/common/button'
import { Input } from '../../components/common/input'
import { Range } from '../../components/common/range'

import styles from './styles.module.scss'
import axios from 'axios';

import { ISignup } from '../../global/interfaces';
import { Link } from 'react-router-dom';
import { Form } from '@unform/web'
import { cpfExpression, userPasswordExpression } from '../../global/regularExpressions';
// 

const Signup: React.FC = () => {
  async function registerUser(userdata: ISignup) {
    const isValidData = userdata.user !== '' &&
                        userdata.email !== '' && 
                        userdata.cpf !== '' &&
                        cpfExpression.test(userdata.cpf) &&
                        userdata.password !== '' &&
                        userPasswordExpression.test(userdata.password)

    isValidData && await axios({
      method: 'post',
      url: 'http://localhost:5000/users',
      data: {
        nome: userdata.user,
        email: userdata.email,
        cpf: userdata.cpf,
        senha: userdata.password
      }
    })
  }

  const initialData = {
    name: '',
    email: '',
    cpf: '',
    password: ''
  }

  return (
    <>
      <div id={styles.loginContainer}>
        <div id={styles.logoContainer}>
          <span>M</span>
        </div>
        <Form initialData={initialData} onSubmit={registerUser}>
          <Input type="text" name="user" placeholder="UsuÃ¡rio" required />
          <Input type="text" name="email" placeholder="Email" required />
          <Input type="text" name="cpf" placeholder="CPF" mask required />
          <Input type="password" name="password" placeholder="Senha" required />
          <Link to="/login">Fazer login</Link>
          <Button type="submit" btnName="Cadastrar-se"/>
        </Form>
      </div>
      <Range />
    </>
  )
}

export default Signup
