import { Button } from '../../components/common/button'
import { Input } from '../../components/common/input'
import { Range } from '../../components/common/range'

import styles from './styles.module.scss'
import axios from 'axios';

import { IUser, ILogin } from '../../global/interfaces';
import { Link } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import { useForm } from "react-hook-form";
import { Form } from '@unform/web'
import { Redirect } from 'react-router';
import { Message } from '../../components/common/message';
import { userPasswordExpression } from '../../global/regularExpressions';

const Login: React.FC = () => {
  const [users, setusers] = useState<IUser[]>([])
  const [accountInvalid, setAccountInvalid] = useState<boolean>(false)
  const { register, handleSubmit } = useForm();
  const [LoggedUser, setLoggedUser] = useState<boolean>(false)

  useEffect(() => {
    async function fetchUsersData() {
      try {
        const response = await axios('http://localhost:5000/users')
        const data = response.data
        setusers(data)
      } catch(e) {
        console.error(e)
      }
    }

    fetchUsersData()
  }, [])

  function onSubmit(data: ILogin) {
    console.log('> ', data)

    const isAccountValid = users?.find(user => {
      const isValid = user.nome === data.user &&
                      user.senha === data.password &&
                      userPasswordExpression.test(data.password)

      return isValid
    })

    console.info(users[0].nome)
    console.info(data.user)
    console.log(users[0].nome === data.user)

    setLoggedUser(isAccountValid ? true : false)
    setAccountInvalid(!isAccountValid ? true : false)
    isAccountValid && localStorage.setItem('loggedUser', 'true')
  }

  const initialData = {
    nome: '',
    senha: ''
  }

  return (
    <>
      <div id={styles.loginContainer}>
        <div id={styles.logoContainer}>
          <span>M</span>
        </div>
        <Form initialData={initialData} onSubmit={handleSubmit(onSubmit)}>
          <Input
            {...register("name")}
            type="text"
            name="user"
            placeholder="Usuário"
            required
          />
          <Input
            {...register("password")}
            type="password"
            name="password"
            placeholder="Senha"
            required
          />
          <Link to="/signup">Cadastrar-se</Link>
          <Button type="submit" btnName="Entrar"/>
        </Form>
        {LoggedUser && <Redirect to={'/'} />}
        {accountInvalid && <Message type="error">Usuário ou senha invalidos</Message>}
      </div>
      <Range />
    </>
  )
}

export default Login
