import styles from './styles.module.scss'

import axios from 'axios'

import { useEffect, useState } from 'react'

import { Spinner } from '../../common/spinner'
import { Card } from '../../common/card'

export function ProductList() {
  const [products, setProducts] = useState<any>([])
  const [isLoaded, setIsLoaded] = useState(false)
  // TODO: create a product interface

  const getContainerHeight = !isLoaded ? {height: '80vh'} : {}

  useEffect(() => {
    async function fetchProductsData() {
      try {
        const response = await axios('http://localhost:5000/products')
        const data = response.data
        setProducts(data)
        setIsLoaded(true)
      } catch(e) {
        console.error(e)
      }
    }

    fetchProductsData()
  }, [])

  return (
    <div id={styles.productList} style={getContainerHeight}>
        <h1>Confira os nossos produtos</h1>
      {!isLoaded && <Spinner />}
      {products?.map((product: any, i: number) => (
          <div className={styles.cardContainer} key={i}>
            <Card image={product.image} title={product.title} price={product.preco} />
          </div>
      ))}
    </div>
  )
}
