import { useEffect, useState } from 'react'
import { Button } from '../../components/common/button'
import { Input } from '../../components/common/input'
import { IUser } from '../../global/interfaces'
import { UsersRepository } from '../../repository/Users'

import styles from './styles.module.scss'

export default function ConfigAccount() {
  const [user, setUser] = useState<IUser | any>()

  useEffect(() => {
    const getCurrentUser = async () => {
      const userId = Number(localStorage.getItem('userId'))

      const user = new UsersRepository()
      const currentUser = await user.getById(userId)
      setUser(currentUser)
      console.log(user)
    }
    getCurrentUser()
  }, [])

  return (
    <div className={styles.container}>
      <div className={styles.card}>
        <h2>Informações da sua conta</h2>
        <Input type='text' name='nome' placeholder='Nome' />
        <Input type='text' name='nome' placeholder='Email' />
        <Input type='text' name='nome' placeholder='CPF' />
        <Input type='text' name='nome' placeholder='Nova senha' /><br />
        <Button type='button' btnName='Salvar' />
      </div>
    </div>
  )
}
