import { Button } from '../../components/common/button'
import { Input } from '../../components/common/input'
import { Range } from '../../components/common/range'

import styles from './styles.module.scss'

import { Link, Redirect } from 'react-router-dom';
import React, { useState } from 'react';
import { useForm } from "react-hook-form";
import { Form } from '@unform/web'
import { VerifyIfExistUser } from './verifyIfExistUser';
import { ILogin } from '../../global/interfaces';
import { Message } from '../../components/common/message';

const Login: React.FC = () => {
  const { register, handleSubmit } = useForm()
  const [userExist, setUserExist] = useState(false)
  const [invalidUser, setInvalidUser] = useState(false)

  const onSubmit = async (data: ILogin) => {
    const existingUser = await VerifyIfExistUser(data.user, data.password)
    !existingUser && setInvalidUser(true)
    setUserExist(existingUser)
  }

  return (
    <>
      <div id={styles.loginContainer}>
        <div id={styles.logoContainer}>
          <span>M</span>
        </div>
        <Form onSubmit={handleSubmit(onSubmit)}>
          <Input
            type="text"
            name="user"
            register={register("user")}
            placeholder="Usuário"
            required
          />
          <Input
            type="password"
            name="password"
            register={register("password")}
            placeholder="Senha"
            required
          />

          <Link to="/signup">Cadastrar-se</Link>
          <Button type="submit" btnName="Entrar"/>

          {userExist && <Redirect to="/" />}
          {invalidUser && <Message type='error'>Usuário invalido</Message>}
        </Form>
      </div>
      <Range />
    </>
  )
}

export default Login
