import { useCallback, useState } from 'react'
import { formatCpf } from '../../../global/formatters'
import styles from './styles.module.scss'
import { useForm } from "react-hook-form";

interface PropsInput {
  type: string
  name: string
  register?: () => any
  placeholder: string
  maxLength?: number
  required?: boolean
  mask?: boolean
}

export function Input(props: PropsInput) {
  const [data, setData] = useState('')
  const { register } = useForm()

  const formatData = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
    (props.name === 'cpf') && setData(formatCpf(event.target.value))
  }, [props.name])

  return (
    <>
      <input
        type={props.type}
        name={props.name}
        {...register("user")}
        placeholder={props.placeholder}
        maxLength={props.maxLength}
        className={styles.customInput}
        value={props.mask ? data : undefined}
        required={props.required}
        onChange={props.mask ? formatData : undefined}
      />
    </>
  )
}
