import { Button } from '../../components/common/button'
import { Input } from '../../components/common/input'
import { Range } from '../../components/common/range'
import styles from './styles.module.scss'

import { IUser, ILogin } from '../../global/interfaces';
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';
import { Form } from '@unform/web'
import { Redirect } from 'react-router';

const Login: React.FC = () => {
  const [users, setusers] = useState<IUser[]>([])
  const [userIsValid, setUserIsValid] = useState<boolean>(false)

  useEffect(() => {
    async function fetchUsersData() {
      try {
        const response = await axios('http://localhost:5000/users')
        const data = response.data
        setusers(data)
      } catch(e) {
        console.error(e)
      }
    }

    fetchUsersData()
  }, [])

  function handleSubmit(data: ILogin) {
    const accountIsValid = users?.find(user => {
      const isValid = user.nome === data.user &&
                      user.senha === data.password

      return isValid
    })

    setUserIsValid(accountIsValid ? true : false)
  }

  const initialData = {
    name: '',
    password: ''
  }

  return (
    <>
      <div id={styles.loginContainer}>
        <div id={styles.logoContainer}>
          <span>M</span>
        </div>
        <Form initialData={initialData} onSubmit={handleSubmit}>
          <Input type="text" name="user" placeholder="UsuÃ¡rio" />
          <Input type="text" name="password" placeholder="Senha" />
          <Link to="/">Cadastrar-se</Link>
          <Button type="submit" btnName="Entrar" />
        </Form>
        {userIsValid && <Redirect to={'/'} />}
      </div>
      <Range />
    </>
  )
}

export default Login
