import styles from "./styles.module.scss";

import { MdAddShoppingCart } from "react-icons/md";
import { Link } from "react-router-dom";
import { SyntheticEvent, useContext, useEffect } from "react";
import { CarrinhoContext } from "../../../providers/carrinho";

interface CardProps {
  image: string
  title: string
  price: string
  id: number
}

export function Card(props: CardProps) {
  const { carrinho, setCarrinho } = useContext(CarrinhoContext)

  function prevent(e: SyntheticEvent) {
    e.preventDefault();
  }

  function addToCart(id: number) {
    setCarrinho([...carrinho, id])
  }

  useEffect(() => {
    console.log(carrinho)
  }, [carrinho])

  return (
    <div className={styles.card}>
      <Link to={`/productDetails/${props.id}`}>
        <div className={styles.productImage}>
          <img src={props.image} alt="product" />
        </div>
        <div className={styles.productInfo}>
          <div className={styles.productName}>{props.title}</div>
          <div className={styles.productPrice}>{props.price}</div>
          <button onClick={prevent} className={styles.addToCar}>
            <MdAddShoppingCart onClick={e => addToCart(props.id)} />
            {carrinho.indexOf(props.id) !== -1 ? 0 : 1}
          </button>
        </div>
      </Link>
    </div>
  );
}
