import { Button } from '../../components/common/button'
import { Input } from '../../components/common/input'
import { Range } from '../../components/common/range'

import styles from './styles.module.scss'

import { Link, Redirect } from 'react-router-dom';
import { Form } from '@unform/web'
import { useForm } from "react-hook-form";

import { IUser } from '../../global/interfaces'
import RegisterUser from './registerUser';
import { useState } from 'react';
import { Message } from '../../components/common/message';

const Signup: React.FC = () => {
  const { register, handleSubmit } = useForm()
  const [status, setStatus] = useState<number | unknown>(0)
  const [showError, setShowError] = useState(false)

  const onSubmit = async (data: IUser) => {
    const statusCode = await RegisterUser(data)
    setStatus(statusCode)
    setShowError(statusCode === 201 ? false : true)
  }

  return (
    <>
      <div id={styles.loginContainer}>
        <div id={styles.logoContainer}>
          <span>M</span>
        </div>
        <Form onSubmit={handleSubmit(onSubmit)}>
          <Input type="text" name="user" register={register("user")} placeholder="Usuário" required />
          <Input type="text" name="email" register={register("email")} placeholder="Email" required />
          <Input type="text" name="cpf" register={register("cpf")} placeholder="CPF" maxLength={14} mask required />
          <Input type="password" name="password" register={register("password")}  placeholder="Senha" required />
          <Link to="/login">Fazer login</Link>
          <Button type="submit" btnName="Cadastrar-se"/>
        </Form>
        {status !== 201 && <Message type="error">Erro ao cadastrar usuário</Message>}
        {showError && <Redirect to="/" />}
      </div>
      <Range />
    </>
  )
}

export default Signup
