import React, { useState, Dispatch } from 'react'

export type CarrinhoContextType = {
  carrinho: Array<number>;
  setCarrinho: Dispatch<any>;
};

export const CarrinhoContext = React.createContext<CarrinhoContextType>({
  carrinho: [],
  setCarrinho: text => console.warn('no carrinho'),
});

export const CarrinhoProvider: React.FC = ({ children }) => {
  const [carrinho, setCarrinho] = useState([]);

  return (
    <CarrinhoContext.Provider value={{carrinho, setCarrinho}}>
      {children}
    </CarrinhoContext.Provider>
  )
}
