import axios from 'axios'
import ApiURL from '../../global/apiURL'
import { IUser } from '../../global/interfaces'

export default async function verifyIfEmailExist (email: string) {
  const users = await axios(`${ApiURL}/users1`)
  const emails = users.data.map((user: IUser) => user.email)
  return emails.includes(email)
}
