import { Button } from '../../components/common/button'
import { Input } from '../../components/common/input'
import { Range } from '../../components/common/range'

import styles from './styles.module.scss'
import axios from 'axios';

import { IUser, ILogin } from '../../global/interfaces';
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { Form } from '@unform/web'
import { Redirect } from 'react-router';
import { Message } from '../../components/common/message';

const Login: React.FC = () => {
  const [users, setusers] = useState<IUser[]>([])
  const [showInvalidUserMessage, setShowInvalidUserMessage] = useState<boolean>(false)
  const [showEmptyFieldsMessage, setShowEmptyFieldsMessage] = useState<boolean>(false)
  const [LoggedUser, setLoggedUser] = useState<boolean>(false)

  useEffect(() => {
    async function fetchUsersData() {
      try {
        const response = await axios('http://localhost:5000/users')
        const data = response.data
        setusers(data)
      } catch(e) {
        console.error(e)
      }
    }

    fetchUsersData()
  }, [])

  function handleSubmit(data: ILogin) {
    const accountIsValid = users?.find(user => {
      const isValid = user.nome === data.user &&
                      user.senha === data.password

      return isValid
    })

    const emptyFields = data.user === '' || data.password === ''

    setShowInvalidUserMessage(!accountIsValid && !emptyFields ? true: false)
    setShowEmptyFieldsMessage(emptyFields ? true : false)
    setLoggedUser(accountIsValid ? true: false)
    accountIsValid && localStorage.setItem('loggedUser', 'true')
  }

  const initialData = {
    name: '',
    password: ''
  }

  return (
    <>
      <div id={styles.loginContainer}>
        <div id={styles.logoContainer}>
          <span>M</span>
        </div>
        <Form initialData={initialData} onSubmit={handleSubmit}>
          <Input type="text" name="user" placeholder="Usuário" required/>
          <Input type="password" name="password" placeholder="Senha" required/>
          <Link to="/">Cadastrar-se</Link>
          <Button type="submit" btnName="Entrar" />
        </Form>
        {LoggedUser && <Redirect to={'/'} />}
        {showInvalidUserMessage && <Message type="error">Usuário ou senha invalidos</Message>}
        {showEmptyFieldsMessage && <Message type="error">Campos vazios</Message>}
      </div>
      <Range />
    </>
  )
}

export default Login
