import React, { useState, Dispatch, SetStateAction } from 'react'

export type LoggedUserContextType = {
  LoggedUser: boolean;
  setLoggedUser: Dispatch<SetStateAction<boolean>>;
};

export const LoggedUserContext = React.createContext<LoggedUserContextType>({
  LoggedUser: false,
  setLoggedUser: text => console.warn('no Product text'),
});

export const LoggedUserProvider: React.FC = ({ children }) => {
  const [LoggedUser, setLoggedUser] = useState(false);

  return (
    <LoggedUserContext.Provider value={{LoggedUser, setLoggedUser}}>
      {children}
    </LoggedUserContext.Provider>
  )
}
