import { useCallback, useState } from 'react'
import styles from './styles.module.scss'

interface PropsInput {
  type: string
  name: string
  placeholder: string
  maxLength?: number
  required?: boolean
  mask?: boolean
}

export function Input({ type, name, placeholder, maxLength, required, mask }: PropsInput) {
  const [data, setData] = useState('')

  const formatData = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
    if (name === 'cpf') {
      setData(event.target.value.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, "$1.$2.$3-$4"))
    } else {
      setData(event.target.value)
    }
  }, [data])

  return (
    <>
      <input
        type={type}
        name={name}
        placeholder={placeholder}
        maxLength={maxLength}
        className={styles.customInput}
        value={data}
        required={required}
        onChange={event => mask && formatData(event)}
      />
    </>
  )
}
